pe.MaxZoomDialog=function(a){pe.init_widget(this,null);this.container=$(this.html(a));this.container.appendTo("body");this.container.dialog({title:"Advance your search?",modal:!0,resizable:!1,width:300,buttons:{Sure:this.callback(this.on_yes_button_click),"Stay here":this.callback(this.on_no_button_click)}})};pe.MaxZoomDialog.prototype.html=function(a){return"<div>Looks like you are looking for something outside of "+a+".<br>Our <i>advanced search</i> page provides more flexible ways to find your party.<br><br>Go there now?</div>"};
pe.MaxZoomDialog.prototype.on_yes_button_click=function(){window.location="/search"};pe.MaxZoomDialog.prototype.on_no_button_click=function(){this.container.dialog("close");this.container.remove()};
