pe.EventCityTemplate=function(a,b){pe.init_widget(this,null);this.container=$(this.event_template(b));a.append(this.container)};
pe.EventCityTemplate.prototype.event_template=function(a){var b=[],c="",c=a.neighborhood_name?pe.templatize('<a href="/neighborhoods/{{neighborhood_id}}" class="blue_link">{{neighborhood_name}}</a>',a):a.city_name;$.each(a.locations,function(a,c){b[a]=pe.templatize('<a href="{{url}}" title="{{name}}" class="border_link">{{name}}</a>',c)});a.location_links=b.join(", ");a.primary_photo=pe.https(pe.assign_photo_size(a.thumbnail,"155","155x105"));a.formatted_start_date=$.datepicker.formatDate("M d",new Date(a.start_date));
a.category_text=a.category.join(", ");return pe.templatize('<li class="event event_pod"><div class="event_wrapper"><div class="event_image float_left"> <a href="/events/{{id}}" class="ConvertToRoundedCorners"><img src="{{primary_photo}}" alt="{{name}}"> </a></div><div class="city_name">'+c+'</div><div class="event_name headline"><a href="/events/{{id}}" title="{{name}}">{{name}}</a></div><div class="type_venue">{{category_text}} / {{location_links}}</div><div class="event_date">{{formatted_start_date}}&nbsp;&nbsp; {{start_time}}</div></div></li>',
a)};
