pe.ClassAnimator=function(a,b,c){pe.init_widget(this,a);this.animation_duration=c||1100;this.current_class=b;this.before_animated_elements=a.find(".BeforeAnimated");this.before_animated_elements.addClass(b);this.animated_elements=a.find(".Animated");this.animated_elements.addClass(b);this.after_animated_elements=a.find(".AfterAnimated");this.after_animated_elements.addClass(b);this.triggers=a.find(".Trigger");this.triggers.click(this.callback(this.trigger_clicked))};
pe.ClassAnimator.prototype.trigger_clicked=function(a){this.animate(this.current_class,$(a.currentTarget).data("class_name")+"","reverse"==$(a.currentTarget).data("direction")+"")};pe.ClassAnimator.prototype.animate=function(a,b,c){c?this.after_animation(a,b):this.before_animation(a,b);this.animation(a,b,c);this.current_class=b};pe.ClassAnimator.prototype.before_animation=function(a,b){this.before_animated_elements.removeClass(a).addClass(b)};
pe.ClassAnimator.prototype.animation=function(a,b,c){this.animated_elements.switchClass(a,b,this.animation_duration,this.callback(function(){c?this.before_animation(a,b):this.after_animation(a,b)}))};pe.ClassAnimator.prototype.after_animation=function(a,b){this.after_animated_elements.removeClass(a).addClass(b)};
