pe.DependencyManager=function(){pe.init_widget(this,null);this.flags=[];this.conditions=[]};pe.DependencyManager.prototype.call_when=function(a,b){_.isArray(a)||(a=[a]);this.conditions.push({flags:a,callback:b,fired:!1});this.fire_callbacks()};pe.DependencyManager.prototype.condition_match=function(a){var b=!0;$.each(a,this.callback(function(a,c){if(-1===this.flags.indexOf(c))return b=!1}));return b};
pe.DependencyManager.prototype.fire_callbacks=function(){$.each(this.conditions,this.callback(function(a,b){!b.fired&&this.condition_match(b.flags)&&(b.fired=!0,b.callback())}))};pe.DependencyManager.prototype.has_flag=function(a){return-1<this.flags.indexOf(a)};pe.DependencyManager.prototype.set_flag=function(a){this.flags.push(a);this.fire_callbacks()};
