pe.InputGroup=function(a){pe.init_widget(this,a);this.inputs=this.container.find("input:visible");this.input_wrapper=this.inputs.closest("ul");this.inputs.each(this.callback(function(a,b){$(b).change(this.callback(this.validate))}))};pe.InputGroup.prototype.has_checked_input=function(){var a=!1;$.each(this.inputs,function(){$(this).is(":checked")&&(a=!0)});return a};
pe.InputGroup.prototype.validate=function(){if(this.has_checked_input())return pe.validation.show_message(this.input_wrapper,""),!0;pe.validation.show_message(this.input_wrapper,"Please make a choice.");return!1};pe.InputGroup.prototype.validate_on_submit=function(){return this.validate()};
