pe.BillboardModule=function(b,c){pe.init_widget(this,b);this.slide_show_div=this.container.find(".SlideShow");this.slide_show_div.html(this.render_photos(c));this.setup_slider();pe.convert_to_rounded_corners(this.container.find(".ConvertToRoundedCorners"))};
pe.BillboardModule.prototype.render_photos=function(b){if(0===b.length)return'<img src="'+pe.https(pe.assign_photo_size("","460","460x360"))+'" alt="" title="">';var c="";$.each(b,this.callback(function(b,a){a.photo=pe.https(pe.assign_photo_size(a.src,"original","460x360"));""===a.title?a.title_div_id="":(a.title_div="title_div_billboard_"+b,a.title_div_id="#"+a.title_div,this.container.append(pe.templatize('<div id="{{title_div}}" class="nivo-html-caption">{{title}}</div>',a)));c=""===a.url?c+pe.templatize('<img src="{{photo}}" alt="" title="{{title_div_id}}">',
a):c+pe.templatize('<a href="{{url}}" target="_self"><img src="{{photo}}" alt="" title="{{title_div_id}}"></a>',a)}));return c};
pe.BillboardModule.prototype.setup_slider=function(){this.slide_show_div.nivoSlider({effect:"fade",slices:15,animSpeed:600,pauseTime:6E3,startSlide:0,directionNav:!0,directionNavHide:!0,controlNav:!0,controlNavThumbs:!1,controlNavThumbsFromRel:!1,controlNavThumbsSearch:".jpg",controlNavThumbsReplace:"_thumb.jpg",keyboardNav:!0,pauseOnHover:!0,manualAdvance:!1,captionOpacity:0.7,beforeChange:function(){},afterChange:function(){},slideshowEnd:function(){}})};
