pe.LoginBox=function(a){pe.init_widget(this,a);this.template='<span id="username">Welcome,&nbsp;&nbsp;<a href="/users/{{login}}" class="blue_link">{{username}}</a></span><span><a href="/logout" class="blue_link">Logout</a></span><span class="hidden WriterJob"><a href="/users/{{login}}/writer_assignments" class="blue_link">My Writer Job</a></span>';this.render()};pe.LoginBox.prototype.is_user_writer=function(){return!!pe.value_from_cookie("writer_login")};
pe.LoginBox.prototype.get_login=function(){void 0===this.login&&(this.login=pe.value_from_cookie("login"));return this.login};pe.LoginBox.prototype.get_username=function(){if(void 0===this.username&&(this.username=pe.value_from_cookie("username")))this.username=this.username.replace("+"," ");return this.username};pe.LoginBox.prototype.render=function(){pe.ajax_loader.header_login.load(this.callback(function(a){this.container.html(a);pe.setup_gigya()}))};
