pe.BrowseByNeighborhood=function(a){pe.init_widget(this,a);this.smalls=a.find(".Small");this.smalls.mouseenter(this.callback(this.activate_pointed_neighborhood));this.display=a.find(".LargeDisplay")};pe.BrowseByNeighborhood.prototype.activate_pointed_neighborhood=function(a){this.activate_neighborhood($(a.currentTarget))};pe.BrowseByNeighborhood.prototype.activate_neighborhood=function(a){this.smalls.filter(".Selected").removeClass("Selected");a.addClass("Selected");this.display.html(a.find(".Large").html())};
