pe.SearchResultVenue=function(a,b,c){pe.init_widget(this,null);this.container=$(this.dom_template(b,c));a.append(this.container);this.container.data("widget",this);new pe.SearchResultCharacterRatingModule(this.container.find(".CharacterRating"),b)};
pe.SearchResultVenue.prototype.dom_template=function(a,b){a.types_text=a.venue_types.join(" / ");a.day_spot_text=a.day_spot?'<span class="day_spot_small icon_types" title="Day Spot"></span>':"";a.night_spot_text=a.night_spot?'<span class="night_spot_small icon_types" title="Night Spot"></span>':"";a.primary_photo=pe.https(pe.assign_photo_size(a.thumbnail,"155","155x105"));a.venue_link_title=pe.link_title_for_venue(a.name,a.city_name,a.venue_types.join(" | "));a.neighborhood_link_title=pe.link_title_for_neighborhood(a.neighborhood_name,
a.city_name);this.add_hotspot_today_image_dom(a,b);var c=pe.ajax_loader.search_neighborhood.find(a.neighborhood_id);a.neighborhood_name=c.name;return pe.templatize('<li class="result venue"><div class="venue_image float_left"><a href="/venues/{{id}}" target="_blank" class="ConvertToRoundedCorners" title="{{venue_link_title}}"><img src="{{primary_photo}}"></a></div><h3 class="venue_name"><a href="/venues/{{id}}" target="_blank" class="headline" title="{{venue_link_title}}">{{name}}</a></h3><p class="types float_left"><span class="type text float_left">{{types_text}} </span><span class="type text float_left">&nbsp;&ndash;&nbsp;</span><a href="/neighborhoods/{{neighborhood_id}}" target="_blank"   class="neighborhood_name text border_link float_left" title="{{neighborhood_link_title}}">{{neighborhood_name}}</a>{{hotspot_today_img}}{{day_spot_text}}{{night_spot_text}}</p><div class="CharacterRating ratings_small_wrapper float_left"></div><div class="desc float_left">{{description}}<div class="read_more_arrow"><a href="/venues/{{id}}" title="{{venue_link_title}}" class="float_right"></a> </div> </div><div class="clear"></div></li>',
a)};pe.SearchResultVenue.prototype.add_hotspot_today_image_dom=function(a,b){var c=new Date(b.get_filter("start_date_or_default")),d=new Date(b.get_filter("end_date_or_default"));a.hotspot_today_img=pe.VenueSearcher.venue_is_hotspot_in_date_range(a,c,d)?'<span class="hot_spot_small icon_types" title="Hotspot Today"></span>':""};
