pe.PageScroller=function(){this.y=0};pe.PageScroller.prototype.add_search_configuration=function(a){var b=this.get_scroll_top();a.add_filter("scroll_top",b)};pe.PageScroller.prototype.get_scroll_top=function(){return $(document).scrollTop()};pe.PageScroller.prototype.read_search_configuration=function(a){this.y=a.filter_value("scroll_top")||0};pe.PageScroller.prototype.scroll_to=function(a){a&&$("html,body").stop().animate({scrollTop:a+"px"},800)};
